import { createApp } from "vue";
import { createStore } from "vuex";
import { createRouter, createWebHistory } from "vue-router";
import App from "./App.vue";
import LoginAdmin from "./components/ComponentsRoutes/LoginAdmin.vue";
import fireModule from "./stores/fireStore/index";
import BaseCard from "./components/ui/BaseCard.vue";
import primaryStats from "./stores/primaryStats/index";
import BaseButton from "./components/ui/BaseButton.vue";
import theStats from "./components/tronOrg/PrimaryStats.vue";
import AddressList from "./components/tronOrg/AddressList.vue";
import SetOwner from "./components/ComponentsRoutes/SetOwner.vue";
import AddressView from "./components/ComponentsRoutes/AddressView.vue";
import regNewAddress from "./components/ComponentsRoutes/RegNewAddress.vue";
import registerAddress from "./components/ComponentsRoutes/RegisterAddress.vue";
import EditCategory from "./components/ComponentsRoutes/EditCategory.vue";

import WatchedAddresses from "./components/ComponentsRoutes/WatchedAddresses.vue";
import RecentTransactions from "./components/ComponentsRoutes/RecentTransactions.vue";

import HashView from "./components/BTC/HashView.vue";
import AddressBView from "./components/BTC/AddressView.vue";

const app = createApp(App);

const router = createRouter({
  history: createWebHistory(),
  routes: [
    { path: "/", redirect: "/recent" },
    { path: "/recent", component:RecentTransactions},
    { path: "/register", component: registerAddress },
    { path: "/register/:id", name:"regNew", component: regNewAddress, props: true },
    { path: "/stats", component: theStats },
    { path: "/address", component: AddressList },
    { path: "/address/:addr", component: AddressView, props: true },
    { path: "/setowner/:address", component: SetOwner },
    { path: "/watched", component: WatchedAddresses},
    { path: "/login", component: LoginAdmin},
    { path: "/edit-cat", component: EditCategory},

    { path: "/hash/:hash", component: HashView, props: true },
    { path: "/addres/:address", component: AddressBView, props: true },
  ],
});

const store = createStore({
  modules: {
    fire: fireModule,
    stats: primaryStats,
  },
});

app.use(store);
app.use(router);
app.component("login-admin", LoginAdmin);
app.component("base-card", BaseCard);
app.component("reg-new", regNewAddress);
app.component("the-stats", primaryStats);
app.component("base-button", BaseButton);
app.component("address-view", AddressView);
app.component("address-list", AddressList);
app.component("register-address", registerAddress);
app.component("edit-category", EditCategory)
app.component("watched-addresses", WatchedAddresses);

app.component("address-bview", AddressBView);
app.component("hash-view", HashView);

app.mount("#app");
